import { useNavigate } from "react-router-dom";
import App, { SendResponse, SetAnswer } from "../App";

export default (props) => {
    var Response;
    const Navigate = useNavigate();

    return (
        <div>
            <h2 className="TextCenter">{props.Question}</h2>
            <input className="AnswerInput" type='text' onChange={event => {Response = event.target.value}}/>
            <p><button onClick= {() => (SendResponse(Response), Navigate("/Wait"), SetAnswer(Response))}>Submit</button></p>
        </div>        
    )
}
