import InputBox from './components/InputBox'
import JoinScreen from './components/JoinScreen';
import LookUp from './components/LookUp';
import Placement from './components/Placement';
import VoteBox from './components/VoteBox';
import WaitForStart from './components/WaitForStart';
import Waiting from './components/Waiting';

import { Route } from 'react-router-dom';

const ws = new WebSocket("ws://localhost:8082");

var PlayerId;

ws.addEventListener("open", () => {
  console.log("connected")
  ws.send(JsonMessage('DataSendTest', "Testdata"));
})

export function JoinGame(Name, GameId) {
  console.log('Player "' + Name + '" is joining game ' + GameId);
  ws.send(JsonMessage('GameId', {Id: GameId, Name: Name}));
}

export function SendResponse(Response) {
  console.log(Response);
  ws.send(JsonMessage('Response', {Id: PlayerId, Response: Response}));
}

export function SendVote(Choice) {
  console.log("Sent vote: " + Choice);
  ws.send(JsonMessage('Vote', {Id: PlayerId, Choice: Choice}));
}

function JsonMessage(Type, Data) {
  return JSON.stringify({type: Type, messageData: JSON.stringify(Data)});
}



function App() {  
  return (
    <div>
      <Switch>
        <Route exact path='/'>
          <JoinScreen/>
        </Route>
        <Route exact path='/Start'>
          <WaitForStart/>
        </Route>
        <Route exact path='/Look'>
          <LookUp/>
        </Route>
        <Route exact path='/Input'>
          <InputBox/>
        </Route>
        <Route exact path='Wait'>
          <Waiting/>
        </Route>
        <Route exact path='Vote'>
          <VoteBox/>
        </Route>
        <Route exact path='Placement'>
          <Placement/>
        </Route>
      </Switch>
    </div>
  );
}

export default App;
