import { JoinGame } from "../App";
import { useNavigate } from 'react-router-dom';


export default () => {
    const Navigate = useNavigate();

    var GmId;
    var PlyNm;
    const Join = () => {
        JoinGame(PlyNm, GmId);
    }
    return (<div>
        <div>
            <p className="JoinInput">Name</p>
            <p className="JoinInput">Game Id</p>
        </div>
        <div>
            <input className="JoinInput" type='text' onChange={event => {PlyNm = event.target.value}}/>
            <input className="JoinInput" type='number' onChange={event => {GmId = event.target.value}}/>
        </div>     
        <button onClick={ () => (Navigate('/Connecting'), Join()) } >Play</button>
    </div>);
}
