import App, { SendResponse } from "../App";

export default (props) => {
    var Response;
    

    return (
        <div>
            <h2>{props.Question}</h2>
            <input type='text' onChange={event => {Response = event.target.value}}/>
            <p><button onClick={Respond}>Submit</button></p>
        </div>        
    )

    function Respond() {
        SendResponse(Response);
    }
}
