import { JoinGame } from "../App";
import { useNavigate, Link } from 'react-router-dom';


function JoinScreen() {
    const Navigate = useNavigate();

    var GmId;
    var PlyNm;
    function Join() {
        //useNavigate('/start');
        JoinGame(PlyNm, GmId);
    }
    return (<div>
        <p>Name</p>
        <input type='text' onChange={event => {PlyNm = event.target.value}}/>
        <p>Game Id</p>
        <input type='number' onChange={event => {GmId = event.target.value}}/>
        <p><button onClick={Join} >Play</button></p>
    </div>);
}
